package basics;

public class Kleine_1x1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub



		for (int i = 1; i < 11; i++) {
			System.out.print((0+i)+"er");
			for(int x = 0; x < 11; x++ ) {

				System.out.print("\t" + i*x);
			

			}
			System.out.println();
		}
	}
	}

