package basics;

public class UngeradeZahlen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int Zahl = 1; Zahl < 101; Zahl++) {
			if (Zahl % 2 == 0) {
				System.out.println("gerade");
			} else {
				System.out.println("ungerade");
			}

		}
	}

}
